import React, { useState } from "react";
import { Button, Link, makeStyles, Container } from "@material-ui/core";
import ClipLoader from "react-spinners/ClipLoader";
import { TextInput, Title } from "../Components";

import { validateNumber } from "../Utils/Validations";

import { useHistory } from "react-router-dom";

import useToken from "../Hooks/useToken";

const axios = require("axios").default;

const useStyles = makeStyles((theme) => ({
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

const SignIn = () => {
  const classes = useStyles();
  const history = useHistory();

  const { setToken } = useToken();
  const [errorMessage, setErrorMessage] = useState("");

  const [loading, setLoading] = useState(false);
  const [userCI, setUserCI] = useState("");
  const [password, setPassword] = useState("");

  const [passwordError, setPassowrdError] = useState(false);
  const [ciError, setCiError] = useState(false);

  const handleInput = (e) => {
    setErrorMessage("");
    const { value, name } = e.target;
    if (name === "ci") {
      setUserCI(value);
      setCiError(!validateNumber(value) || value.length !== 8);
    } else if (name === "password") {
      setPassword(value);
      setPassowrdError(value.length < 5);
    }
  };

  const userData = {
    ci: userCI,
    password: password,
  };

  const createEvent = () => {
    setLoading(true);
    setErrorMessage("");
    axios
      .post("http://localhost:8080/seguridad/login", userData)
      .then(function (response) {
        const { data } = response;
        if (data.message === "Persona no registrada") {
          setErrorMessage("Usuario o contrase単a incorrecta");
          setLoading(false);
        } else {
          data["ci"] = userCI;
          setToken(data);

          history.push("/");
          setLoading(false);
        }
      })
      .catch(function (error) {
        setLoading(false);
        console.log("Error", error);
      });
  };

  return (
    <Container component="main" maxWidth="xs">
      <Title text="Ingresar" white h2 />

      <form noValidate>
        <TextInput
          id={"ci"}
          onChange={handleInput}
          label={"C.I"}
          name={"ci"}
          error={ciError}
          errorMessage={"Ingrese un numero de CI valido"}
          type={"number"}
        />
        <TextInput
          id={"password"}
          onChange={handleInput}
          label={"Contrase単a"}
          name={"password"}
          error={passwordError}
          errorMessage={"Ingrese un contrase単a mas larga"}
          type={"password"}
        />

        <ClipLoader
          color={"blue"}
          loading={loading}
          css={{ marginTop: 20 }}
          size={30}
        />

        {errorMessage && <div style={{ color: "red" }}> {errorMessage} </div>}

        <Button
          type="submit"
          fullWidth
          disabled={
            loading ||
            ciError ||
            userCI.length === 0 ||
            password.length === 0 ||
            passwordError ||
            errorMessage
          }
          variant="contained"
          color="primary"
          className={classes.submit}
          onClick={createEvent}
        >
          Iniciar sesion
        </Button>
        <Link
          href="/signup"
          variant="body2"
          onClick={() => {
            history.push("/signup");
          }}
        >
          {"多No estas registrado? Registrarse"}
        </Link>
      </form>
    </Container>
  );
};

export default SignIn;
