import React, { useState, useEffect } from "react";
import {
  Container,
  Button,
  makeStyles,
  Card,
  CardContent,
  Typography,
} from "@material-ui/core";
import useToken from "../Hooks/useToken";
import moment from "moment";
import "moment/locale/es";
import { useHistory } from "react-router-dom";
import { Title } from "../Components";
const axios = require("axios").default;

const capitalize = (phrase) => {
  return phrase.charAt(0).toUpperCase() + phrase.slice(1);
};

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: "100%",
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  root: {
    minWidth: 275,
    background: "#4287f5",
    marginBottom: 20,
    marginRight: 20,
  },
  root2: {
    minWidth: 275,
    background: "#4287f5",
    marginBottom: 20,
  },
  title: {
    fontSize: 22,
    color: "white",
    fontWeight: "500",
  },
}));

const formatDate = (date) => {
  const dia = moment(date).format("dddd");
  const fecha = moment(date).format("LL");
  return dia + " " + fecha;
};

const Main = () => {
  const { token, setToken } = useToken();
  const history = useHistory();
  const classes = useStyles();

  const [userCI, setUserCI] = useState("");
  const [userName, setUserName] = useState("");
  const [place, setPlace] = useState("");
  const [firstDosis, setFirstDosis] = useState("");
  const [secondDosis, setSecondDosis] = useState("");
  const [fdReceived, setFdReceived] = useState(false);
  const [sdReceived, setSdReceived] = useState(false);
  const [hasContact, setHasContact] = useState(false);

  const refreshUser = (ci) => {
    axios
      .post("http://localhost:8080/coronavirus/refresh", { ci: ci })
      .then(function (response) {
        const { data } = response;
        if (data) {
          const { dosis1, dosis2, fecha_vac_1, fecha_vac_2, contacto } = data;
          if (dosis1) {
            setFdReceived(dosis1);
          }
          if (dosis2) {
            setSdReceived(dosis2);
          }
          if (fecha_vac_1) {
            setFirstDosis(fecha_vac_1);
          }
          if (fecha_vac_2) {
            setSecondDosis(fecha_vac_2);
          }
          if (contacto) {
            contacto.includes("NO estuvo")
              ? setHasContact(false)
              : setHasContact(true);
          }
        }
      })
      .catch(function (error) {
        console.log("Error", error);
      });
  };

  useEffect(() => {
    let ciUser = "";
    if (token) {
      const {
        fecha_vac_1,
        fecha_vac_2,
        apellidos,
        dosis1,
        dosis2,
        nombre,
        locacion,
        ci,
      } = token;
      setUserName(nombre + " " + apellidos);
      setPlace(locacion);
      const fechaPrimerDosis =
        moment(fecha_vac_1).format("dddd e MMMM") +
        " a las " +
        ("HORA", moment(fecha_vac_1).format("LT")) +
        " hs";
      const fechaSegundaDosis =
        moment(fecha_vac_2).format("dddd e MMMM") +
        " a las " +
        ("HORA", moment(fecha_vac_2).format("LT")) +
        " hs";
      setFirstDosis(capitalize(fechaPrimerDosis));
      setSecondDosis(capitalize(fechaSegundaDosis));
      setFdReceived(dosis1);
      setSdReceived(dosis2);
      setUserCI(ci);
      ciUser = ci;
    }
    refreshUser(ciUser);
  }, []);

  if (!token) {
    return (
      <div
        style={{
          flexDirection: "column",
          display: "flex",
          justifyContent: "space-evenly",
          height: 500,
        }}
      >
        <Button
          type="submit"
          variant="contained"
          size="large"
          color="primary"
          onClick={() => {
            history.push("/signup");
          }}
        >
          REGISTRARSE
        </Button>

        <Button
          type="submit"
          variant="contained"
          size="large"
          color="primary"
          onClick={() => {
            history.push("/signin");
          }}
        >
          INGRESAR
        </Button>
      </div>
    );
  }

  return (
    <Container>
        <Title text="Usuario" />
        <h3 style={{ marginBottom: 30 }}>{userName}</h3>
        <Button
          type="submit"
          variant="contained"
          size="medium"
          className={classes.submit}
          color="primary"
          onClick={() => {
            setToken();
            sessionStorage.clear();
          }}
        >
          Salir
        </Button>
    </Container>
  );
};

export default Main;
