import React, { useState, useEffect } from "react";
import {
  Button,
  Checkbox,
} from "@material-ui/core";
import { DataGrid } from '@material-ui/data-grid';
import "moment/locale/es";
import { useHistory } from "react-router-dom";
import { Title } from "../Components";

import { getUsers } from "../Utils/api";
import Container from "./Styles/Container";

const Admin = () => {
  const history = useHistory();
  const user = useState(JSON.parse(localStorage.getItem('user')));
  const [users, setUsers] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    if (user) {
      setIsAdmin(user[0].admin);// FIXME: admin?
    }
  }, []);

  useEffect(() => {
    getUsers().then(response => {
      if (response.data.error) {
        alert(response.data.message)
      } else {
        console.log(response.data);
        setUsers(response.data);
      }
    });
  }, []);

  if (!user[0] || !isAdmin) { // FIXME: token
    return (
      <div
        style={{
          flexDirection: "column",
          display: "flex",
          justifyContent: "space-evenly",
          height: 500,
        }}
      >
          No tienes permiso para acceder a esta pÃ¡gina
        <Button
          variant="contained"
          size="large"
          color="primary"
          onClick={() => {
            history.push("/");
          }}
        >
          Regresar
        </Button>
      </div>
    );
  }

  return (
    <Container>
        <Title text="Admin" /> 
        {DataTable(users)}
        <Button
          type="submit"
          variant="contained"
          size="medium"
          color="primary"
          onClick={() => {
            localStorage.clear();
          }}
        >
          Salir
        </Button>
    </Container>
  );
};

export default Admin;


const columns = [
  { field: 'id', headerName: 'ID', width: 70 },
  { field: 'username', headerName: 'Nombre de usuario', width: 90 },
  { field: 'nombre', headerName: 'Nombre', width: 90 },
  { field: 'apellido', headerName: 'Apellido', width: 90 },
  { field: 'mail', headerName: 'Email', width: 90 },
  {
    field: 'admin',
    headerName: 'Admin',
    renderCell: (params) => (
        <Checkbox
          checked={false}
          onChange={console.log(params)}
          inputProps={{ 'aria-label': 'primary checkbox' }}
        />
    ),
  },
];

const rows = [
  { id: 1, lastName: 'Snow', firstName: 'Jon', age: 35 },
  { id: 2, lastName: 'Lannister', firstName: 'Cersei', age: 42 },
  { id: 3, lastName: 'Lannister', firstName: 'Jaime', age: 45 },
  { id: 4, lastName: 'Stark', firstName: 'Arya', age: 16 },
  { id: 5, lastName: 'Targaryen', firstName: 'Daenerys', age: null },
  { id: 6, lastName: 'Melisandre', firstName: null, age: 150 },
  { id: 7, lastName: 'Clifford', firstName: 'Ferrara', age: 44 },
  { id: 8, lastName: 'Frances', firstName: 'Rossini', age: 36 },
  { id: 9, lastName: 'Roxie', firstName: 'Harvey', age: 65 },
];

const DataTable = (asdasd) => {
  return (
    <div style={{ height: 400, width: '100%' }}>
      <DataGrid rows={rows} columns={columns} pageSize={5} checkboxSelection onSelectionModelChange={itm => console.log(itm)} />
    </div>
  );
}
