import React, { useState, useEffect } from "react";

import { Button, Container, makeStyles } from "@material-ui/core";
import { useLocation } from "react-router-dom";
import { Title, TextInput } from "../Components";
import ClipLoader from "react-spinners/ClipLoader";
import moment from "moment";
import "moment/locale/es";
import { useHistory } from "react-router-dom";
import { validateEmail } from "../Utils/Validations";

const axios = require("axios").default;

const useStyles = makeStyles((theme) => ({
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

const CancelReservation = () => {
  moment.locale("es");
  const classes = useStyles();
  const history = useHistory();

  const [errorMessage, setErrorMessage] = useState("");

  const location = useLocation();

  const [loading, setLoading] = useState(false);
  const [dataFound, setDataFound] = useState(false);
  const [userCi, setUserCi] = useState("47899002");
  const [userEmail, setUserEmail] = useState("mail@gmail.com");
  const [reservationNumber, setReservationNumber] = useState("1");

  const [ciError, setCiError] = useState(false);
  const [reservationNumberError, setReservationNumberError] = useState(false);
  const [emailError, setEmailError] = useState(false);

  const handleInput = (e) => {
    setErrorMessage("");
    const { value, name } = e.target;
    if (name === "userEmail") {
      setUserEmail(value);
      setEmailError(!validateEmail(value));
    } else if (name === "userCI") {
      setUserCi(value);
      setCiError(value.length !== 8);
    } else if (name === "reservationNumber") {
      setReservationNumber(value);
      setReservationNumberError(value.length < 1);
    }
  };

  const searchReservation = () => {
    setLoading(true);
    setErrorMessage("");
    const data = {
      id: reservationNumber,
      mail: userEmail,
      ci: userCi,
    };
    axios
      .post("http://localhost:8080/coronavirus/vacunacion/cancelar", data)
      .then(function (response) {
        const { data } = response;
        if (!data.message.includes("cancelada con exito")) {
          setErrorMessage("No se encontro ninguna reserva");
          setLoading(false);
        } else {
          setDataFound(true);
          setLoading(false);
        }
      })
      .catch(function (error) {
        setLoading(false);
        setDataFound(false);
        console.log("Error", error);
      });
  };

  useEffect(() => {}, []);

  return (
    <Container component="main" maxWidth="sm">
      {!dataFound && (
        <div>
          <Title text="Cancelar Reserva" />

          <TextInput
            id={"userCI"}
            onChange={handleInput}
            label={"Cedula de Identidad"}
            name={"userCI"}
            error={ciError}
            errorMessage={"Ingrese una C.I valida"}
            type={"number"}
          />
          <TextInput
            id={"userEmail"}
            onChange={handleInput}
            label={"Correo Electronico"}
            name={"userEmail"}
            error={emailError}
            errorMessage={"Ingrese un email valido"}
            type={"email"}
          />
          <TextInput
            id={"reservationNumber"}
            onChange={handleInput}
            label={"Código de reserva"}
            name={"reservationNumber"}
            error={reservationNumberError}
            errorMessage={"Ingrese un numero valido"}
            type={"number"}
          />

          {!errorMessage && (
            <ClipLoader
              color={"blue"}
              loading={loading}
              css={{ marginVertical: 50 }}
              size={30}
            />
          )}

          {errorMessage && <div style={{ color: "red" }}> {errorMessage} </div>}

          <Button
            type="submit"
            fullWidth
            className={classes.submit}
            disabled={
              loading ||
              reservationNumberError ||
              reservationNumber.length < 1 ||
              ciError ||
              emailError ||
              errorMessage
            }
            variant="contained"
            color="primary"
            onClick={searchReservation}
          >
            Buscar Reserva
          </Button>
        </div>
      )}

      {dataFound && (
        <div>
          <Title text="Reserva Cancelada con Exito" white />

          <Button
            type="submit"
            fullWidth
            disabled={loading}
            variant="contained"
            color="primary"
            onClick={() => {
              setLoading(false);
              setDataFound(false);
              setUserCi("");
              setReservationNumber("");
              setErrorMessage("");
              history.push("/");
            }}
          >
            Volver
          </Button>
        </div>
      )}
    </Container>
  );
};

export default CancelReservation;
