import React, { useState, useEffect } from "react";
import {
  Button,
  Link,
  makeStyles,
  Container,
  TextField,
} from "@material-ui/core";
import ClipLoader from "react-spinners/ClipLoader";
import { TextInput, Title } from "../Components";
import { validateName, validateNumber } from "../Utils/Validations";

import { register } from "../Utils/api";

import { useHistory } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: "100%",
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

const SignUp = () => {
  const classes = useStyles();
  const history = useHistory();

  const [loading, setLoading] = useState(false);
  const [username, setUserName] = useState("");
  const [nombre, setNombre] = useState("");
  const [apellido, setApellido] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // const [ciError, setCiError] = useState(false);
  // const [nameError, setnameError] = useState(false);
  // const [surnameError, setSurnameError] = useState(false);
  // const [passwordError, setPassowrdError] = useState(false);
  // const [numberError, setNumberError] = useState(false);

  // const [errorMessage, setErrorMessage] = useState("");

  const handleInput = (e) => {
    // setErrorMessage("");
    const { value, name } = e.target;
    switch (name) {
      case "username":
        setUserName(value);
        // setCiError(!validateNumber(value) || value.length !== 8);
        break;
      case "nombre":
        setNombre(value);
        // setnameError(!validateName(value) || value.length <= 2);
        break;
      case "apellido":
        setApellido(value);
        // setSurnameError(!validateName(value) || value.split(" ").length < 2);
        break;
      case "email":
        setEmail(value);
        // setNumberError(!validateNumber(value) || value.length < 5);
        break;
      case "password":
        setPassword(value);
        // setPassowrdError(value.length < 5);
        break;
      default:
        break;
    }
  };

  const data = {
    nombre: nombre,
    apellido: apellido,
    username: username,
    email: email,
    password: password,
  };

  const registrarse = () => {
    // setErrorMessage("");
    // setLoading(true);
    const { data } = register(data);
    if (data.message === "Persona ya registrada.") {
      // setErrorMessage("Persona ya registrada");
      setLoading(false);
    } else {
      history.push("/signin");
      setLoading(false);
    }
  };


  return (
    <Container component="main" maxWidth="xs">
      <Title text=" Crear Cuenta" white />

      <form className={classes.form} noValidate>
        <TextInput
          id={"userName"}
          onChange={handleInput}
          label={"Nombre de usuario"}
          name={"userName"}
          // error={ciError}
          errorMessage={"Ingrese un nombre de usuario valido"}
          type={"text"} // puede estar mal
        />
        <TextInput
          id={"nombre"}
          onChange={handleInput}
          label={"Nombre"}
          name={"nombre"}
          // error={nameError}
          errorMessage={"Ingrese un nombre valido"}
        />
        <TextInput
          id={"apellido"}
          onChange={handleInput}
          label={"Apellido"}
          name={"apellido"}
          // error={surnameError}
          errorMessage={"Ingrese sus apellidos correctamente"}
        />
        <TextInput
          id={"email"}
          onChange={handleInput}
          label={"Email"}
          name={"email"}
          // error={numberError}
          errorMessage={"Ingrese un email valido"}
          type={"email"} // puede estar mal
        />
        <TextInput
          id={"password"}
          onChange={handleInput}
          label={"Contraseña"}
          name={"password"}
          // error={passwordError}
          errorMessage={"Ingrese un contraseña mas larga"}
          type={"password"}
        />

        <ClipLoader
          color={"blue"}
          loading={loading}
          css={{ marginTop: 20 }}
          size={30}
        />

        {/* {errorMessage && <div style={{ color: "red" }}> {errorMessage} </div>} */}

        <Button
          type="submit"
          fullWidth
          // disabled={
          //   loading ||
          //   userCI.length === 0 ||
          //   userName.length === 0 ||
          //   userSurnames.length === 0 ||
          //   number.length === 0 ||
          //   ciError ||
          //   nameError ||
          //   surnameError ||
          //   passwordError ||
          //   numberError ||
          //   errorMessage
          // }
          variant="contained"
          color="primary"
          className={classes.submit}
          onClick={registrarse}
        >
          CREAR CUENTA
        </Button>
        <Link
          href="/signin"
          variant="body2"
          onClick={() => {
            history.push("/signin");
          }}
        >
          {"¿Ya estas registrado? Ingresar"}
        </Link>
      </form>
    </Container>
  );
};

export default SignUp;
