import React, { useState } from "react";
import { Button, Link, makeStyles, Container } from "@material-ui/core";
import ClipLoader from "react-spinners/ClipLoader";
import { TextInput, Title } from "../Components";
import { login } from "../Utils/api";

import { validateNumber } from "../Utils/Validations";

import { useHistory } from "react-router-dom";

import useToken from "../Hooks/useToken";

const useStyles = makeStyles((theme) => ({
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

const SignIn = () => {
  const classes = useStyles();
  const history = useHistory();

  const { setToken } = useToken();
  // const [errorMessage, setErrorMessage] = useState("");

  const [loading, setLoading] = useState(false);
  const [username, setUserName] = useState("");
  const [password, setPassword] = useState("");

  // const [passwordError, setPassowrdError] = useState(false);
  // const [ciError, setCiError] = useState(false);

  const handleInput = (e) => {
    // setErrorMessage("");
    const { value, name } = e.target;
    if (name === "userName") {
      setUserName(value);
      // setCiError(value.length < 5);
    } else if (name === "password") {
      setPassword(value);
      // setPassowrdError(value.length < 5);
    }
  };

  const iniciarSesion = () => {
    // setLoading(true);
    // setErrorMessage("");
    const user = login(userName, password);
    console.log(user);
    // validar funco?
    setToken(user);
  };

  return (
    <Container component="main" maxWidth="xs">
      <Title text="Ingresar" white h2 />

      <form noValidate>
        <TextInput
          id={"userName"}
          onChange={handleInput}
          label={"Nombre de usuario"}
          name={"userName"}
          // error={ciError}
          errorMessage={"Ingrese un nombre de usuario valido"}
          type={"number"}
        />
        <TextInput
          id={"password"}
          onChange={handleInput}
          label={"Contrase単a"}
          name={"password"}
          // error={passwordError}
          errorMessage={"Ingrese un contrase単a mas larga"}
          type={"password"}
        />

        <ClipLoader
          color={"blue"}
          loading={loading}
          css={{ marginTop: 20 }}
          size={30}
        />

        {/* {errorMessage && <div style={{ color: "red" }}> {errorMessage} </div>} */}

        <Button
          // type="submit"
          fullWidth
          // disabled={
          //   loading ||
          //   ciError ||
          //   userCI.length === 0 ||
          //   password.length === 0 ||
          //   passwordError ||
          //   errorMessage
          // }
          variant="contained"
          color="primary"
          // className={classes.submit}
          onClick={iniciarSesion}
        >
          Iniciar sesion
        </Button>
        <Link
          href="/signup"
          variant="body2"
          onClick={() => {
            history.push("/signup");
          }}
        >
          {"多No estas registrado? Registrarse"}
        </Link>
      </form>
    </Container>
  );
};

export default SignIn;
