import React, { useState, useEffect } from "react";

import { Container } from "@material-ui/core";
import { useLocation } from "react-router-dom";
import { Title } from "../Components";
import moment from "moment";
import "moment/locale/es";

const capitalize = (phrase) => {
  return phrase.charAt(0).toUpperCase() + phrase.slice(1);
};

const formatDate = (date) => {
  const dia = moment(date).format("dddd");
  const fecha = moment(date).format("LL");
  return (
    dia + " " + fecha + " a las " + ("HORA", moment(date).format("LT")) + " hs"
  );
};

const Date = () => {
  moment.locale("es");

  const location = useLocation();

  const [userCi, setUserCi] = useState("");
  const [place, setPlace] = useState("");
  const [firstDosis, setFirstDosis] = useState("");
  const [secondDosis, setSecondDosis] = useState("");
  const [reservationNumber, setReservationNumber] = useState("");

  useEffect(() => {
    if (location.state) {
      const { ci, fecha1, fecha2, locacion, numReserva } =
        location.state.detail;
      setUserCi(ci);
      setPlace(locacion);
      setReservationNumber(numReserva);

      const fechaPrimerDosis = formatDate(fecha1);

      const fechaSegundaDosis = formatDate(fecha2);

      setFirstDosis(capitalize(fechaPrimerDosis));
      setSecondDosis(capitalize(fechaSegundaDosis));
    }
  }, []);

  return (
    <Container component="main">
      <Title text="Numero de Reserva" />
      <h3>{reservationNumber}</h3>
      <Title text="Vacunatorio" />
      <h3>{place}</h3>
      <Title text="Primera Dosis" />
      <h3>{firstDosis}</h3>
      <Title text="Segunda Dosis" />
      <h3>{secondDosis}</h3>
    </Container>
  );
};

export default Date;
