import React, { useState } from "react";
import { Button, makeStyles, Container, TextField } from "@material-ui/core";
import ClipLoader from "react-spinners/ClipLoader";
import { Title } from "../Components";
import moment from "moment";
import useToken from "../Hooks/useToken";

import { useHistory } from "react-router-dom";

const axios = require("axios").default;

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: "100%",
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

const Alert = () => {
  const { token } = useToken();

  const classes = useStyles();
  const history = useHistory();

  const [loading, setLoading] = useState(false);
  const [fecha, setFecha] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [called, setCalled] = useState(false);

  const handleInput = (e) => {
    setErrorMessage("");
    const { value, name } = e.target;
    switch (name) {
      case "date":
        const format = moment(value).format("L");
        setFecha(format + " 00:00");
        break;
      default:
        break;
    }
  };

  const createEvent = () => {
    const { ci } = token;

    const data = {
      ci: ci,
      fecha: fecha,
    };
    setErrorMessage("");
    setLoading(true);
    axios
      .post("http://localhost:8080/coronavirus/alertapositivo", data)
      .then(function (response) {
        setCalled(true);
        setLoading(false);
      })
      .catch(function (error) {
        setLoading(false);
        setCalled(false);
        console.log("Error", error);
      });
  };
  return (
    <Container component="main" maxWidth="xs">
      {!called && (
        <div>
          <Title text="Alertar Positivo" white />
          <form className={classes.form} noValidate>
            <TextField
              id="date"
              label="Fecha"
              type="date"
              name="date"
              margin="normal"
              fullWidth
              onChange={(text) => {
                handleInput(text);
              }}
              defaultValue={fecha}
              className={classes.textField}
              InputLabelProps={{
                shrink: true,
              }}
            />

            <ClipLoader
              color={"blue"}
              loading={loading}
              css={{ marginTop: 20 }}
              size={30}
            />

            {errorMessage && (
              <div style={{ color: "red" }}> {errorMessage} </div>
            )}

            <Button
              type="submit"
              fullWidth
              disabled={loading || errorMessage || !fecha}
              variant="contained"
              color="primary"
              className={classes.submit}
              onClick={createEvent}
            >
              ALERTAR
            </Button>
          </form>
        </div>
      )}
      {called && (
        <div>
          <Title text="Gracias por notificar" white h2 />
          <Button
            type="submit"
            fullWidth
            disabled={loading || errorMessage}
            variant="contained"
            color="primary"
            className={classes.submit}
            onClick={() => history.push("/")}
          >
            Volver
          </Button>
        </div>
      )}
    </Container>
  );
};

export default Alert;
